import React, { useState } from "react";

import MoviesList from "./components/MoviesList";
import "./App.css";

function App() {
  const [movies, setMovies] = useState([]);

  function fetchMoviesHandler() {
    // Default is GET
    // As fetch is an asynchronous function we need to make sure the result is already load into the app
    fetch("https://swapi.dev/api/films/")
      .then((response) => {
        response.json(); // Built-in method that transform the response into objects
      })
      .then((data) => {
        const transformedMOvies = data.result.map((moviesData) => {
          return {
            id: moviesData.episode_id,
            title: moviesData.title,
            openingText: moviesData.opening_crawl,
            releaseDate: moviesData.release_date,
          };
        });
        setMovies(data.results); // We gotta make sure the name of the properties are the same we are going to use
      });
  }

  return (
    <React.Fragment>
      <section>
        <button>Fetch Movies</button>
      </section>
      <section>
        <MoviesList movies={movies} />
      </section>
    </React.Fragment>
  );
}

export default App;
